const initState = {
  filters: {
    search: "",
    status: "All",
    priority: "[]",
  },
  todoList: [
    {
      id: 1,
      name: "Learn Yoga",
      completed: false,
      priority: "Medium",
    },
    {
      id: 2,
      name: "Learn React",
      completed: false,
      priority: "High",
    },
    {
      id: 3,
      name: "Learn JS",
      completed: false,
      priority: "Low",
    },
  ],
};

const rootReducer = (state = initState, action) => {
  /*
{
     type: 'todoList/addTodo',
     payload: {
     id: 4,
     name: "Learn Saga",
     completed: false,
     priority: "Low",  
     }
}
 */
  switch (action.type) {
    case "todoList/addTodo":
      return {
        ...state,
        todoList: [...state.todoList, action.payload],
      };
    case "filters/searchFillterChange":
      return {
        ...state,
        filters: [...state.filters],
      };
    default:
      return state;
  }
};

export default rootReducer;
