const rootReducer = (state, action) => {
  /*
{
     type: 'todoList/addTodo',
     payload: {
     id: 4,
     name: "Learn Saga",
     completed: false,
     priority: "Low",  
     }
}
 */
  switch (action.type) {
    case "todoList/addTodo":
      return {
        ...state,
        todoList: [...state.todoList, action.payload],
      };
    case "filters/searchFillterChange":
      return {
        ...state,
        filters: {
          ...state,
          search: action.payload,
        },
      };
    default:
      return state;
  }
};

export default rootReducer;
